import { observable,  action } from 'mobx';

class CounterStore {

    @observable
    _count = 5;

    get count() {
        return this._count;
    }
    @action
    increment() {
        this._count ++;
    }

    @action
    decrement() {
        this._count --;
    }

}
// export default CounterStore; 사용하는 곳에서 new 해서 사용해야 함
export default new CounterStore;
