import { observable } from 'mobx';

class CounterStore {

    @observable
    _count = 5;

    increment() {
        this._count ++;
    }

    decrement() {
        this._count --;
    }

}
// export default CounterStore; 사용하는 곳에서 new 해서 사용해야 함
export default new CounterStore;
