import { observable, action, computed, makeObservable, toJS } from 'mobx';

class TodoStore {

    constructor() { // 데이터 변경이 되었을 때 변경이 됨
        makeObservable(this);
    }

    @observable
    _todo = {} // private로 생각  id, title, data

    // todo 데이터들을 담을 todos 배열객체 
    @observable
    _todos = [];

    // get 메서드, 데이터만 만들어 놓고 return (다른 곳에 내보내기 위해서) 
    get todo() {
        return this._todo;
    }

    get todos() {
        // return this._todos; // 잘 들어감 아래에
        // return this._todos ? this._todos.slice() : [];
        return toJS(this._todos);
    }

    @action
    setTodoProps(name, value) {
        this._todo = {
            ...this._todo,
            // name이 title이면 title이 들어가고 data면 data가 들어가도록
            [name] : value
        }
    }

    @action
    addTodo(todo) {
        // 추가
        this.todos.push(todo);
    }
}
export default new TodoStore();
